INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "EN", 1, "<p><span>Thousands of businesses and organisations across Europe are asked to respond to a questionnaire that focuses on:</span></p><ul><li>General safety and health risks in the workplace and how they are managed</li><li><a href=\"https://osha.europa.eu/en/themes/psychosocial-risks-and-stress\" target=\"_blank\">Psychosocial risks</a>, such as stress, bullying and harassment</li><li>Drivers of and barriers to OSH management</li><li><a href=\"https://osha.europa.eu/en/themes/leadership-and-worker-participation\" target=\"_blank\">Worker participation</a> in safety and health practices.</li></ul>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "BG", 0, "<p><span>Множество предприятия и организации в Европа са поканени да отговорят на въпросник относно:</span></p><ul><li>общи рискове за безопасността и здравето на работното място и начини за управлението им</li><li><a href=\"https://osha.europa.eu/bg/themes/psychosocial-risks-and-stress\" target=\"_blank\">психосоциални рискове</a>, като стрес, малтретиране и тормоз</li><li>мотивиращи фактори и пречки при управлението на безопасността и здравето при работа (БЗР)</li><li><a href=\"https://osha.europa.eu/bg/themes/leadership-and-worker-participation\" target=\"_blank\">участие на служителите</a> в практиките за безопасност и здраве.</li></ul>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "CS", 0, "<div>Tisíce podniků a organizací v celé Evropě jsou požádány o poskytnutí odpovědí na dotazník, který se zaměřuje na:</div><div><ul><li>všeobecná rizika BOZP a jejich řízení,</li><li><a href=\"https://osha.europa.eu/cs/themes/psychosocial-risks-and-stress\" target=\"_blank\">psychosociální rizika</a>, jako je stres, šikana a obtěžování,</li><li>podněty a překážky při řízení BOZP,</li><li><a href=\"https://osha.europa.eu/cs/themes/leadership-and-worker-participation\" target=\"_blank\">účast zaměstnanců</a>&nbsp;na postupech v rámci BOZP.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "DA", 0, "<div>Tusindvis af virksomheder og organisationer i hele Europa opfordres til at udfylde et spørgeskema om:</div><div><ul><li>generelle arbejdsmiljørisici, og hvordan de håndteres</li><li><a href=\"https://osha.europa.eu/da/themes/psychosocial-risks-and-stress\" target=\"_blank\">psykosociale risici</a>&nbsp;såsom stress, mobning og chikane</li><li>drivkræfter og hindringer for effektiv arbejdsmiljøhåndtering</li><li><a href=\"https://osha.europa.eu/da/themes/leadership-and-worker-participation\" target=\"_blank\">inddragelse af medarbejdere</a>&nbsp;i arbejdsmiljøspørgsmål.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "DE", 0, "<div>Tausende von Unternehmen und Organisationen aus ganz Europa sind gebeten, einen Fragebogen zu den folgenden Schwerpunktthemen auszufüllen:</div><div><ul><li>Allgemeine Sicherheits- und Gesundheitsrisiken am Arbeitsplatz und der Umgang mit diesen Risiken</li><li><a href=\"https://osha.europa.eu/de/themes/psychosocial-risks-and-stress\" target=\"_blank\">Psychosoziale Risiken</a>&nbsp;wie Stress, Mobbing und Belästigung</li><li>Triebkräfte und Hindernisse beim Management von Sicherheit und Gesundheitsschutz bei der Arbeit</li><li><a href=\"https://osha.europa.eu/de/themes/leadership-and-worker-participation\" target=\"_blank\">Arbeitnehmerbeteiligung</a>&nbsp;an Maßnahmen im Bereich Sicherheit und Gesundheitsschutz.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "ET", 0, "<div>Tuhandetele Euroopa ettevõtetele ja organisatsioonidele saadetakse küsimustik järgmistel teemadel:</div><div><ul><li>üldised ohutus- ja terviseriskid töökohal ning nende juhtimine;</li><li><a href=\"https://osha.europa.eu/et/themes/psychosocial-risks-and-stress\" target=\"_blank\">psühhosotsiaalsed riskid</a>, nagu stress, kiusamine ja ahistamine;</li><li>tööohutuse ja töötervishoiu juhtimist soodustavad ja takistavad tegurid;</li><li><a href=\"https://osha.europa.eu/et/themes/leadership-and-worker-participation\" target=\"_blank\">töötajate osalemine</a>&nbsp;ohutus- ja tervishoiumeetmetes.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "EL", 0, "<div>Χιλιάδες επιχειρήσεις και οργανισμοί σε όλη την Ευρώπη καλούνται να απαντήσουν σε ερωτηματολόγιο που επικεντρώνεται στα εξής:</div><div><ul><li>Γενικοί κίνδυνοι για την ασφάλεια και την υγεία στον χώρο εργασίας και ο τρόπος διαχείρισής τους</li><li><a href=\"https://osha.europa.eu/el/themes/psychosocial-risks-and-stress\" target=\"_blank\">Ψυχοκοινωνικοί κίνδυνοι</a>, ιδίως το άγχος, ο εκφοβισμός και η παρενόχληση</li><li>Κίνητρα και φραγμοί στη διαχείριση της ΕΑΥ</li><li><a href=\"https://osha.europa.eu/el/themes/leadership-and-worker-participation\" target=\"_blank\">Συμμετοχή των εργαζομένων</a>&nbsp;στις πρακτικές ασφάλειας και υγείας.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "ES", 0, "<div>Miles de empresas y organizaciones de toda Europa responden a un cuestionario que se centra en los siguientes temas:</div><div><ul><li>Seguridad general y riesgos para la salud en el trabajo y su gestión</li><li><a href=\"https://osha.europa.eu/es/themes/psychosocial-risks-and-stress\" target=\"_blank\">Riesgos psicosociales</a>&nbsp;como el estrés, la intimidación y el acoso</li><li>Factores impulsores y obstáculos en la gestión de la SST</li><li><a href=\"https://osha.europa.eu/es/themes/leadership-and-worker-participation\" target=\"_blank\">Participación de los trabajadores</a>&nbsp;en las prácticas de seguridad y salud.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "FR", 0, "<div>Des milliers d'entreprises et d'organisations en Europe sont invitées à remplir un questionnaire axé sur les thèmes suivants:</div><div><ul><li>les risques généraux pour la santé et la sécurité sur le lieu de travail et leur gestion;</li><li>les&nbsp;<a href=\"https://osha.europa.eu/fr/themes/psychosocial-risks-and-stress\" target=\"_blank\">risques psychosociaux</a>, tels que le stress, l'intimidation et le harcèlement;</li><li>les motivations et les obstacles à la gestion de la santé et de la sécurité au travail (SST);</li><li>la&nbsp;<a href=\"https://osha.europa.eu/fr/themes/leadership-and-worker-participation\" target=\"_blank\">participation des travailleurs</a>&nbsp;à la SST.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "HR", 0, "<div>Tisuće poduzeća i organizacija diljem Europe pozvano je na sudjelovanje u upitniku usmjerenom na</div><div><ul><li>rizike za opću sigurnost i zdravlje na radnom mjestu te na načine upravljanja tim rizicima.</li><li><a href=\"https://osha.europa.eu/hr/themes/psychosocial-risks-and-stress\" target=\"_blank\">Psihosocijalni rizici</a>, poput stresa, uznemiravanja i maltretiranja</li><li>Pokretači i prepreke upravljanju sigurnošću i zdravljem na radu</li><li><a href=\"https://osha.europa.eu/hr/themes/leadership-and-worker-participation\" target=\"_blank\">Sudjelovanje radnika</a>&nbsp;u praksi sigurnosti i zdravlja</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "IS", 0, "<div>Þúsundir fyrirtækja og stofnanna í Evrópu hafa verið beðin að fylla út spurningalista sem beinir sjónum sínu að:</div><div><ul><li>Almennum vinnuverndaráhættum og stjórnun þeirra</li><li><a href=\"https://osha.europa.eu/is/themes/psychosocial-risks-and-stress\" target=\"_blank\">Sálfélagslegum áhættum</a>&nbsp;eins og streitu, einelti og áreitni</li><li>Hvötum og hindrunum á sviði vinnuverndarstjórnunar</li><li><a href=\"https://osha.europa.eu/is/themes/leadership-and-worker-participation\" target=\"_blank\">Starfsmannaþátttaka</a>&nbsp;í vinnuvernd</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "IT", 0, "<div>Migliaia di imprese e organizzazioni di tutta Europa sono invitate a rispondere a un questionario incentrato sulle seguenti tematiche:</div><div><ul><li>rischi generali per la sicurezza e la salute sul lavoro e loro gestione;</li><li><a href=\"https://osha.europa.eu/it/themes/psychosocial-risks-and-stress\" target=\"_blank\">rischi psicosociali</a>&nbsp;quali stress, bullismo e violenza;</li><li>incentivi e ostacoli alla gestione della SSL;</li><li><a href=\"https://osha.europa.eu/it/themes/leadership-and-worker-participation\" target=\"_blank\">partecipazione dei lavoratori</a>&nbsp;alle prassi in materia di sicurezza e salute.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LV", 0, "<div>Tūkstošiem uzņēmumu un organizāciju pārstāvju visā Eiropā atbild uz uzņēmumu apsekojuma (ESENER) anketas jautājumiem, kas īpaši pievēršas</div><div><ul><li>vispārējie ar darba drošību un veselību saistītie riska veidi darbavietā un kā tos pārvaldīt;</li><li><a href=\"https://osha.europa.eu/lv/themes/psychosocial-risks-and-stress\" target=\"_blank\">psihosociālie riski</a>&nbsp;– tādi kā stress, iebiedēšana un uzmākšanās;</li><li>virzītājspēki un šķēršļi DDVA pārvaldībā;</li><li><a href=\"https://osha.europa.eu/lv/themes/leadership-and-worker-participation\" target=\"_blank\">darbinieku iesaistīšanās</a>&nbsp;darba drošības un veselības aizsardzības praksē.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LT", 0, "<div>Tūkstančių visos Europos verslo įmonių ir organizacijų prašoma atsakyti į klausimyno klausimus apie:</div><div><ul><li>bendrus darbo vietoje darbuotojų saugai ir sveikatai kylančios rizikos veiksnius ir jų valdymo metodus;</li><li><a href=\"https://osha.europa.eu/lt/themes/psychosocial-risks-and-stress\" target=\"_blank\">psichosocialinės rizikos veiksnius</a>, pvz., stresą, patyčias ir priekabiavimą;</li><li>DSS valdymo paskatas ir kliūtis;</li><li><a href=\"https://osha.europa.eu/lt/themes/leadership-and-worker-participation\" target=\"_blank\">darbuotojų dalyvavimą</a>&nbsp;įgyvendinant praktines saugos ir sveikatos gerinimo priemones.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "HU", 0, "<div>Európa szerte több ezer vállalkozást és szervezetet kértek fel egy kérdőív kitöltésére, amelynek témái:</div><div><ul><li>Az általános munkahelyi biztonsági és egészségügyi kockázatok, illetve azok kezelése</li><li><a href=\"https://osha.europa.eu/hu/themes/psychosocial-risks-and-stress\" target=\"_blank\">Pszichoszociális kockázatok</a>, például stressz, megfélemlítés és zaklatás</li><li>A munkahelyi biztonsági és egészségvédelmi irányítást elősegítő és akadályozó tényezők</li><li><a href=\"https://osha.europa.eu/hu/themes/leadership-and-worker-participation\" target=\"_blank\">Dolgozói részvétel</a>&nbsp;az egészségvédelmi és biztonsági gyakorlatokban</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "MT", 0, "<div>Eluf ta' negozji u organizzazzjonijiet minn madwar l-Ewropa huma mitluba jwieġbu kwestjonarju li jiffoka fuq:</div><div><ul><li>Ir-riskji tas-sigurtà u tas-saħħa ġenerali fuq il-post tax-xogħol u kif inhuma ġestiti</li><li><a href=\"https://osha.europa.eu/mt/themes/psychosocial-risks-and-stress\" target=\"_blank\">Riskji Psikosoċjali</a>, bħal stress, bullying u fastidju</li><li>Motivaturi u ostakli għall-ġestjoni tas-Saħħa u s-Sigurtà fuq il-Post tax-Xogħol</li><li><a href=\"https://osha.europa.eu/mt/themes/leadership-and-worker-participation\" target=\"_blank\">Parteċipazzjoni tal-ħaddiem</a>&nbsp;fil-prattiki tas-saħħa u s-sigurtà.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "NL", 0, "<div>Duizenden bedrijven en organisaties in heel Europa wordt verzocht een vragenlijst in te vullen, waarin de nadruk vooral ligt op:</div><div><ul><li>algemene risico's voor de veiligheid en gezondheid op het werk (VGW) en hoe daarmee wordt omgegaan;</li><li><a href=\"https://osha.europa.eu/nl/themes/psychosocial-risks-and-stress\" target=\"_blank\">psychosociale risico's</a>&nbsp;zoals stress, intimidatie en pesten;</li><li>drijvende krachten achter en belemmeringen voor VGW-beheer.</li><li><a href=\"https://osha.europa.eu/nl/themes/leadership-and-worker-participation\" target=\"_blank\">werknemersparticipatie</a>&nbsp;in veiligheids- en gezondheidsmaatregelen.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "NO", 0, "<div>Tusenvis av virksomheter og organisasjoner over hele Europa blir bedt om å fylle ut et spørreskjema som fokuserer på:</div><div><ul><li>Generelle HMS-risikoer på arbeidsplassen og hvordan de håndteres</li><li><a href=\"https://osha.europa.eu/no/themes/psychosocial-risks-and-stress\" target=\"_blank\">Psykososiale risikoer</a>&nbsp;som stress, mobbing og trakassering</li><li>Pådrivere og hindringer for HMS-styring</li><li><a href=\"https://osha.europa.eu/no/themes/leadership-and-worker-participation\" target=\"_blank\">Arbeidstakermedvirkning</a>&nbsp;i HMS-arbeidet.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "PL", 0, "<div>Tysiące przedsiębiorstw i organizacji w całej Europie poproszono o odpowiedź na kwestionariusz dotyczący:</div><div><ul><li>ogólnego bezpieczeństwa i zagrożeń dla zdrowia w miejscu pracy oraz sposobów zarządzania nimi</li><li><a href=\"https://osha.europa.eu/pl/themes/psychosocial-risks-and-stress\" target=\"_blank\">Zagrożenia psychospołeczne</a>&nbsp;takie jak stres, mobbing i szykanowanie</li><li>Czynniki motywujące do działań w zakresie zarządzania BHP i przeszkody w takich działaniach</li><li><a href=\"https://osha.europa.eu/pl/themes/leadership-and-worker-participation\" target=\"_blank\">Udział pracowników</a>&nbsp;w praktykach w zakresie BHP.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "PT", 0, "<div>Milhares de empresas e organizações de toda a Europa são convidadas a responder a um questionário com enfoque na:</div><div><ul><li>Segurança geral e riscos para a saúde no local de trabalho e na forma como são geridos</li><li><a href=\"https://osha.europa.eu/pt/themes/psychosocial-risks-and-stress\" target=\"_blank\">Riscos psicossociais</a>, nomeadamente o stresse, a intimidação e o assédio</li><li>Impulsionadores e obstáculos no âmbito da gestão da SST</li><li><a href=\"https://osha.europa.eu/pt/themes/leadership-and-worker-participation\" target=\"_blank\">Participação dos trabalhadores</a>&nbsp;em matéria de práticas de segurança e saúde.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "RO", 0, "<div>Mii de întreprinderi și organizații din toată Europa sunt invitate să răspundă la un chestionar care se referă la:</div><div><ul><li>Riscurile generale pentru securitatea și sănătatea la locul de muncă și modalități de gestionare a acestora</li><li><a href=\"https://osha.europa.eu/ro/themes/psychosocial-risks-and-stress\" target=\"_blank\">Riscurile psihosociale</a>, precum stresul, intimidarea și hărțuirea</li><li>Stimulente și impedimente în gestionarea SSM</li><li><a href=\"https://osha.europa.eu/ro/themes/leadership-and-worker-participation\" target=\"_blank\">Participarea lucrătorilor</a>&nbsp;la practicile de securitate și sănătate.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SK", 0, "<div>Tisíce podnikov a organizácií v Európe sú vyzvané, aby zodpovedali na dotazník venovaný týmto témam:</div><div><ul><li>všeobecné riziká pre bezpečnosť a ochranu zdravia na pracovisku a ich riadenie,</li><li><a href=\"https://osha.europa.eu/sk/themes/psychosocial-risks-and-stress\" target=\"_blank\">psychosociálne riziká</a>, ako je stres, šikanovanie a obťažovanie,</li><li>stimuly a prekážky pri riadení BOZP,</li><li><a href=\"https://osha.europa.eu/sk/themes/leadership-and-worker-participation\" target=\"_blank\">participácia zamestnancov</a>&nbsp;na postupoch BOZP.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SL", 0, "<div>Na tisoče podjetij in organizacij po Evropi je pozvanih, da odgovorijo na vprašalnik, ki se osredotoča na:</div><div><ul><li>splošna tveganja na področju varnosti in zdravja na delovnem mestu in kako jih obvladati;</li><li><a href=\"https://osha.europa.eu/sl/themes/psychosocial-risks-and-stress\" target=\"_blank\">psihosocialna tveganja</a>, kot so stres, ustrahovanje in nadlegovanje;</li><li>motive in ovire pri upravljanja varnosti in zdravja pri delu;</li><li><a href=\"https://osha.europa.eu/sl/themes/leadership-and-worker-participation\" target=\"_blank\">sodelovanje delavcev</a>&nbsp;pri praksah na področju varnosti in zdravja pri delu.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "FI", 0, "<div>Tuhansia yrityksiä ja organisaatioita eri puolilta Eurooppaa pyydetään vastaamaan kyselyyn, joka käsittelee</div><div><ul><li>yleisiä työpaikkojen terveys- ja turvallisuusriskejä sekä niiden hallintaa</li><li><a href=\"https://osha.europa.eu/fi/themes/psychosocial-risks-and-stress\" target=\"_blank\">psykososiaalisia riskejä</a>, kuten stressiä, kiusaamista ja häirintää</li><li>työsuojelua edistäviä tekijöitä ja sen esteitä</li><li><a href=\"https://osha.europa.eu/fi/themes/leadership-and-worker-participation\" target=\"_blank\">työntekijöiden osallistumista</a>&nbsp;työsuojelun käytäntöihin.</li></ul></div>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SV", 0, "<div>Tusentals företag och organisationer runt om i Europa har uppmanats att svara på ett frågeformulär om</div><div><ul><li>allmänna arbetsmiljörisker och hur de hanteras.</li><li><a href=\"https://osha.europa.eu/sv/themes/psychosocial-risks-and-stress\" target=\"_blank\">Psykosociala risker</a>&nbsp;som stress, mobbning och trakasserier</li><li>Drivkrafter och hinder för hantering av arbetsmiljön.</li><li><a href=\"https://osha.europa.eu/sv/themes/leadership-and-worker-participation\" target=\"_blank\">Arbetstagarmedverkan</a>&nbsp;i praxis för arbetsmiljöfrågor</li></ul></div>");

