INSERT INTO literal (id, chart_id, section_id, type) VALUES (@literalId, null, @sectionId, "ESENER_GENERAL_INFORMATION");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "EN", 1, "The results from these interviews are complemented by secondary analyses involving a series of in-depth studies that focus on specific topics. Quantitative and qualitative research methodologies are applied in these studies to help better understand the main findings from the survey.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "BG", 0, "Резултатите от тези допитвания се допълват с вторични анализи, разглеждащи конкретни теми в дълбочина. Прилагат се качествени и количествени методи на анализ с цел по-добро изучаване на основните констатации от проучването.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "CS", 0, "<p><span>Výsledky vyplývající z těchto dotazníků doplňují sekundární analýzy zahrnující řadu hloubkových studií, které se zaměřují na konkrétní témata. V těchto studiích se uplatňují metodiky kvantitativního i kvalitativního výzkumu s cílem lépe porozumět hlavním zjištěním průzkumu.</span></p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "DA", 0, "Resultaterne fra denne spørgeundersøgelse suppleres af sekundæranalyser, som omfatter en række tilbundsgående undersøgelser med fokus på specifikke emner. Kvantitative og kvalitative forskningsmetoder anvendes i disse undersøgelser for at bidrage til en bedre forståelse af de væsentligste undersøgelsesresultater.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "DE", 0, "Die Ergebnisse dieser Befragungen werden um Sekundäranalysen ergänzt, die eine Reihe von eingehenden Untersuchungen mit konkreten Themenschwerpunkten beinhalten. Für ein besseres Verständnis der wichtigsten Ergebnisse der Erhebung kommen bei diesen Untersuchungen quantitative und qualitative Forschungsmethoden zum Einsatz.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "ET", 0, "Küsimustike vastuseid täiendavad tugianalüüsid, sealhulgas spetsiifilisi teemasid käsitlev põhjalike uuringute seeria. Neis uuringutes kasutatakse põhitulemuste paremaks tõlgendamiseks kvantitatiivseid ja kvalitatiivseid uurimismeetodeid.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "EL", 0, "Τα αποτελέσματα από τις συνεντεύξεις αυτές συμπληρώνονται με δευτερεύουσες αναλύσεις, οι οποίες περιλαμβάνουν μια σειρά από διεξοδικές μελέτες και επικεντρώνονται σε συγκεκριμένα θέματα. Στις εν λόγω μελέτες εφαρμόζονται ποσοτικές και ποιοτικές μεθοδολογίες έρευνας για την καλύτερη κατανόηση των κυριότερων πορισμάτων της έρευνας.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "ES", 0, "Los resultados de estos cuestionarios se complementan con análisis secundarios, los cuales incluyen una serie de estudios más detallados que cubren temas específicos. En dichos estudios, se aplican metodologías cuantitativas y cualitativas para facilitar la comprensión de las principales conclusiones de la encuesta.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "FR", 0, "Les résultats de ces questionnaires sont complétés par des analyses secondaires comprenant une série d'études approfondies centrées sur des sujets spécifiques. Des méthodes de recherche quantitatives et qualitatives sont utilisées dans le cadre de ces études, afin de mieux comprendre les principaux résultats de l'enquête.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "HR", 0, "Rezultatima tih intervjua dodatne su popratne analize koje uključuju seriju dubinskih studija usmjerenih na specifične teme. U tim su studijama primijenjene kvalitativne i kvantitativne metodologije istraživanja za lakše razumijevanje glavnih rezultata istraživanja.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "IS", 0, "Niðurstöðurnar úr þessum viðtölum eru studdar af greiningu á fyrirliggjandi gögnum sem byggja á ítarlegum rannsóknum sem beina sjónum sínum að ákveðnum efnum. Í þessum rannsóknum er beitt eigindlegri og megindlegri aðferðafræði til að öðlast betri skilning á helstu niðurstöðum rannsóknarinnar.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "IT", 0, "I risultati di queste interviste sono integrati da analisi secondarie basate su una serie di studi approfonditi riguardanti argomenti specifici. In tali studi sono applicate metodologie di ricerca quantitativa e qualitativa per aiutare a comprendere meglio le conclusioni principali dell’indagine.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LV", 0, "Šajās intervijās gūtie rezultāti tiek papildināti ar sekundārām analīzēm, ieskaitot dažus padziļinātus pētījumus, kuru uzmanības centrā ir konkrētas tēmas. Lai labāk izprastu aptaujas galvenos konstatējumus, šajos pētījumos tiek pielietota kvantitatīvo un kvalitatīvo pētījumu metodoloģija.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LT", 0, "Šių apklausų rezultatus papildo antrinės analizės duomenys, gauti vykdant nuodugnius konkrečių teminių sričių tyrimus. Šiuose tyrimuose taikomos kiekybinės ir kokybinės metodikos, padedančios geriau suprasti pagrindines tyrimo išvadas.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "HU", 0, "Az interjúk eredményeit másodlagos elemzéssel egészítik ki, amely számos, specifikus kérdésekkel kapcsolatos részletes tanulmányt tartalmaz. Ezekben a tanulmányokban mennyiségi és minőségi kutatási módszertanokat alkalmaznak annak érdekében, hogy érthetőbbé tegyék a felmérés legfőbb megállapításait.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "MT", 0, "Ir-riżultati li ħarġu minn dawn l-intervisti huma kkumplimentati b'analiżi sekondarja li tinvolvi serje ta' studji fil-fond li jiffokaw fuq suġġetti speċifiċi. F'dawn l-istudji huma applikati metodoloġiji tar-riċerka kwantitattivi u kwalitattivi biex jgħinu fil-fehim aħjar tas-sejbiet ewlenin li ħarġu mill-istħarriġ.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "NL", 0, "De resultaten van deze enquête zullen worden aangevuld met secundaire analyses in de vorm van een reeks diepgaande studies over specifieke onderwerpen. Bij deze studies zullen kwantitatieve en kwalitatieve onderzoeksmethoden worden toegepast om de voornaamste bevindingen van de enquête beter te begrijpen.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "NO", 0, "Resultatene fra disse intervjuene suppleres av sekundæranalyser av en rekke dyptgående studier som fokuserer på bestemte emner. Studiene bruker kvantitative og kvalitative forskningsmetoder for å gjøre det lettere å forstå de viktigste funnene fra undersøkelsen.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "PL", 0, "Wyniki tych wywiadów uzupełniono o wtórne analizy obejmujące serię pogłębionych badań skupiających się na konkretnych zagadnieniach. W badaniach tych zastosowano ilościową i jakościową metodologię badań w celu lepszego zrozumienia głównych wniosków ankiety.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "PT", 0, "Os resultados destas entrevistas são complementados por análises secundárias que envolvem uma série de estudos aprofundados concentrados em tópicos específicos. São aplicadas nestes estudos metodologias de pesquisa quantitativas e qualitativas para ajudar a compreender melhor as principais conclusões do inquérito.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "RO", 0, "Rezultatele acestor interviuri sunt completate de analize secundare care implică o serie de studii aprofundate vizând teme specifice. În aceste studii se aplică metode de cercetare cantitative și calitative pentru a facilita înțelegerea constatărilor principale ale sondajului.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SK", 0, "Výsledky rozhovorov doplní druhotná analýza, ktorej súčasťou budú hĺbkové štúdie zamerané na osobitné témy. Na lepšie pochopenie hlavných zistení z prieskumu sa v týchto štúdiách použijú kvantitatívne a kvalitatívne výskumné metódy.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SL", 0, "Rezultati iz teh vprašalnikov so dopolnjeni s sekundarnimi analizami, ki vključujejo vrsto poglobljenih študij, osredotočenih na posamezne teme. Zaradi boljšega razumevanja glavnih ugotovitev raziskave so v teh študijah uporabljene kvantitativne in kvalitativne raziskovalne metodologije.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "FI", 0, "Haastatteluista saatavia tuloksia täydentävät sekundaarianalyysit, joihin kuuluu sarja perusteellisia tutkimuksia erityisistä aiheista. Näissä tutkimuksissa sovelletaan sekä kvantitatiivisia että kvalitatiivisia menetelmiä, jotta saataisiin parempi käsitys kyselytutkimuksen keskeisimmistä tuloksista.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SV", 0, "Resultaten från dessa intervjuer kompletteras av sekundäranalyser som omfattar ingående studier med inriktning på särskilda ämnen. Kvantitativa och kvalitativa forskningsmetoder tillämpas i dessa studier för bättre förståelse av de viktigaste resultaten från undersökningen.");

