INSERT INTO literal (id, chart_id, section_id, type) VALUES (@literalId, null, @sectionId, "ESENER_2009_INFORMATION");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "EN", 1, "The first ESENER, carried out in 2009, involved nearly 36,000 interviews with managers and OSH representatives. All EU Member States were covered, as were Croatia, Turkey, Norway and Switzerland — 31 countries in all. The interviewees worked in private and public sector organisations with 10 or more employees.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "BG", 0, "В проучването ESENER, чието първо издание се провежда през 2009 г., са проведени близо 36 000 интервюта с ръководители и представители по БЗР. Включени са всички държави членки на ЕС, както и Хърватия, Турция, Норвегия и Швейцария — общо 31 държави. Интервютата са проведени в организации в частния и публичния сектор с 10 или повече работници и служители.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "CS", 0, "První průzkum ESENER, který se uskutečnil v roce 2009, zahrnoval téměř 36 000 rozhovorů s vedoucími pracovníky a zástupci zaměstnanců pro BOZP. Zahrnoval všechny členské státy EU, jakož i 
Chorvatsko, Turecko, Norsko a Švýcarsko, celkem tedy 31 zemí. Respondenti pracovali v organizacích soukromého či veřejného sektoru s10 či více zaměstnanci.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "DA", 0, "Den første ESENER-undersøgelse, som blev gennemført i 2009, omfattede næsten 36 000 interviews med ledere og arbejdsmiljørepræsentanter. Alle EU's medlemsstater samt Kroatien, Tyrkiet, Norge og Schweiz var omfattet af undersøgelsen, som således dækkede i alt 31 lande. Deltagerne kom fra organisationer i den private og offentlige sektor med mindst 10 ansatte.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "DE", 0, "Die erste Unternehmenserhebung ESENER-1 wurde im Jahr 2009 durchgeführt und umfasste knapp 36 000 Befragungen von Managern und Gesundheits- und Sicherheitsbeauftragten. Es beteiligten sich alle EU-Mitgliedstaaten sowie die Kroatien, Türkei, Norwegen und die Schweiz – insgesamt also 31 Länder. Die Befragten arbeiteten in Organisationen des privaten und des öffentlichen Sektors mit jeweils mindestens zehn Mitarbeitern.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "ET", 0, "2009. aastal korraldatud esimese ESENER-uuringu raames tehti ligi 36 000 intervjuud juhtide ning tööohutus- ja töötervishoiuvaldkonna esindajatega. Hõlmatud olid kõik Euroopa Liidu liikmesriigid, samuti Horvaatia, Türgi, Norra ja Šveits – kokku 31 riiki. Intervjueeritud isikud töötasid vähemalt kümne töötajaga organisatsioonides avalikust või erasektorist.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "EL", 0, "Η πρώτη έρευνα ESENER διεξήχθη το 2009 και περιλαμβάνει σχεδόν 36.000 συνεντεύξεις με διοικητικά στελέχη και εκπροσώπους των εργαζομένων σε θέματα ΕΑΥ. Καλύπτει όλα τα κράτη μέλη της ΕΕ, καθώς και την Κροατία, Τουρκία, τη Νορβηγία και την Ελβετία, δηλαδή 31 χώρες. Οι ερωτώμενοι ήταν υπάλληλοι επιχειρήσεων του ιδιωτικού και του δημόσιου τομέα με δυναμικό τουλάχιστον 10 εργαζομένων.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "ES", 0, "En la primera ESENER, llevada a cabo en 2009, se realizaron cerca de 36 000 entrevistas con directivos y encargados de la SST. Todos los Estados miembros de la UEs, así como Croacia, Turquía, Noruega y Suiza, 31 países en total. Los entrevistados trabajaban en organizaciones de los sectores público y privado que contaban con 10 o más empleados.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "FR", 0, "La première enquête ESENER, menée en 2009, a inclus près de 36 000 entretiens avec des directeurs et des représentants de la SST. Tous les États membres de l'UE étaient couverts, de même que la Croatie, la Turquie, la Norvège et la Suisse, soit 31 pays au total. Les personnes interrogées travaillaient dans des organisations du secteur privé ou public employant au moins 10 employés.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "HR", 0, "Prvo istraživanje ESENER provedeno je 2009. te je uključivalo približno 36 000 intervjua s upraviteljima u poduzećima i zastupnicima za sigurnost i zdravlje na radu. Istraživanje je obuhvatilo sve države članice EU-a te Hrvatsku, Tursku, Norvešku i Švicarsku, odnosno 31 zemlju ukupno. Ispitanici su bili zaposleni u organizacijama iz privatnog i javnog sektora, s deset ili više zaposlenih.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "IS", 0, "Fyrsta ESENER, sem fór fram árið 2009, innihélt næstum því 36.000 viðtöl við yfirmenn og vinnuverndarfulltrúa. Könnunin tók til allra aðildarríkja ESB svo og Króatía, Tyrklands, Noregs og Sviss — 31 lands í allt. Viðtöl fóru fram í fyrirtækjum í einka- og opinbera geiranum með 10 starfsmenn eða fleiri.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "IT", 0, "Nell’ambito del primo ESENER, condotto nel 2009, sono state effettuate quasi 36 000 interviste con responsabili e rappresentanti di SSL. Tutti gli Stati membri dell’UE hanno partecipato all’indagine, oltre a Croazia, Turchia, Norvegia e Svizzera, per un totale di 31 paesi. Gli intervistati lavoravano in organizzazioni del settore privato e pubblico da 10 o più dipendenti.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LV", 0, "Pirmais ESENER apsekojums, kas tika veikts 2009. gadā, aptvēra gandrīz 36 000 aptaujāto vadītāju un DDVA pārstāvju. Bija iekļautas visas ES dalībvalstīs, kā arī Horvātija, Turcija, Norvēģija un Šveice – kopskaitā 31 valsts. Aptaujātie strādāja gan privātā, gan valsts sektora organizācijās ar 10 vai vairāk darbiniekiem.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LT", 0, "2009 m. atliekant pirmąją ESENER apklausą, surengta beveik 36 000 pokalbių su vadovais ir DSS atstovais. Apklausoje iš viso dalyvavo 31 šalis: visos ES valstybės narės, taip pat Kroatija, Turkija, Norvegija ir Šveicarija. Apklausti respondentai iš privačiojo ir viešojo sektoriaus organizacijų, kuriose dirba 10 ar daugiau darbuotojų.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "HU", 0, "Az első, 2009-ben végzett ESENER felméréshez közel 36 000 interjút készítettek a vezetőkkel és munkahelyi biztonsági és egészségvédelmi képviselőkkel. A felmérésben az EU összes tagállama részt vett, Horvátország, Törökországgal, Norvégiával és Svájccal együtt – összesen 31 ország. Az interjúalanyok a 10 vagy több főt foglalkoztató magán- és állami szektorbeli szervezeteknél dolgoztak.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "MT", 0, "L-ewwel ESENER, li twettaq fl -2009, involva kważi 36,000 intervisti ma' maniġers u r-rappreżentanti tal-OSH. L-Istati Membri tal-UE kollha kienu koperti, bħalma kienu il-Kroazja, it-Turkija, in-Norveġja u l-Isvizzera-31 pajjiż b'kollox. L-intervistati ħadmu fl-organizzazzjonijiet tas-settur privat u pubbliku b'10 impjegati jew aktar.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "NL", 0, "De eerste Esener-enquête, die in 2009 werd uitgevoerd, was gebaseerd op bijna 36 000 interviews met leidinggevenden en VGW-vertegenwoordigers. De enquête had betrekking op alle EU-lidstaten plus Kroatië, Turkije, Noorwegen en Zwitserland (31 landen in totaal). De geïnterviewden werkten bij particuliere en publieke organisaties met tien of meer werknemers.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "NO", 0, "Den første ESENER-undersøkelsen, som ble gjennomført i 2009, omfattet nærmere 36 000 intervjuer med ledere og HMS-representanter. Alle EU-medlemsstatene i EU pluss Kroatia, Tyrkia, Norge og Sveits ble dekket – 31 land i alt. Intervjuobjektene arbeidet i organisasjoner i privat og offentlig sektor med mer enn 10 ansatte.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "PL", 0, "Pierwsze badanie ESENER, przeprowadzone w 2009 r., obejmowało blisko 36 000 rozmów z kierownictwem i przedstawicielami ds. BHP. W badaniu uczestniczyły wszystkie państwa członkowskie UE, a także Chorwacja, Turcja, Norwegia i Szwajcaria – ogółem 31 krajów. Rozmowy przeprowadzono z pracownikami organizacji sektora prywatnego i publicznego zatrudniających co najmniej 10 pracowników.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "PT", 0, "O primeiro inquérito ESENER, realizado em 2009, incluiu cerca de 36 000 entrevistas a gestores e representantes de SST. Foram abrangidos todos os Estados-Membros da UE, bem como a Croácia, Turquia, a Noruega e a Suíça – 31 países no total. Os entrevistados trabalhavam em organizações públicas ou privadas com 10 ou mais trabalhadores.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "RO", 0, "Primul sondaj ESENER, efectuat în 2009, a cuprins aproape 36 000 de interviuri cu manageri și reprezentanți ai departamentelor de SSM. Au fost incluse toate statele membre ale UE, precum și Croația, Turcia, Norvegia și Elveția – în total 31 de țări. Respondenții lucrau în organizații cu cel puțin 10 angajați, din sectorul privat și din cel public.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SK", 0, "V prvom prieskume ESENER, ktorý sa uskutočnil v roku 2009, bolo zahrnutých takmer 36 000 rozhovorov s vedúcimi pracovníkmi a zástupcami pre BOZP. Týkal sa všetkých členských štátov EÚ, ako aj Chorvátska, Turecka, Nórska a Švajčiarska – spolu 31 krajín. Účastníci rozhovorov pracovali v organizáciách zo súkromného a verejného sektora s minimálne 10 zamestnancami.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SL", 0, "V prvi raziskavi ESENER, ki je bila opravljena leta 2009, je bilo anketiranih skoraj 36 000 vodstvenih delavcev ter predstavnikov za zdravje in varnost pri delu. Vključene so bile vse države članice EU ter Hrvaška, Turčija, Norveška in Švica – skupno 31 držav. Anketiranci so delali v javnih in zasebnih organizacijah z deset ali več zaposlenimi.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "FI", 0, "Ensimmäinen ESENER-tutkimus tehtiin vuonna 2009, ja siihen sisältyi lähes 36 000 johtajille ja työsuojeluedustajille tehtyä haastattelua. Kaikki EU:n jäsenvaltiot olivat mukana tutkimuksessa, samoin kuin Kroatia, Turkki, Norja ja Sveitsi. Maita oli kaikkiaan 31. Haastatellut työskentelivät yksityisen ja julkisen sektorin organisaatioissa, joissa oli vähintään 10 työntekijää.");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SV", 0, "Den första Esenerundersökningen genomfördes 2009 och omfattade nära 36 000 intervjuer med chefer och skyddsombud. Alla EU:s medlemsstater samt Kroatien, Turkiet, Norge och Schweiz omfattades – allt som allt 31 länder. Intervjupersonerna arbetade vid både privata och offentliga företag med minst tio anställda.");

