INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LV", 0, "<ul><li>darbinieku iesaistīšanās DDVA pārvaldībā —&nbsp;<a href=\"https://osha.europa.eu/lv/tools-and-publications/publications/worker-participation-management-occupational-safety-health/view\">pārskati</a>,&nbsp;<a href=\"https://osha.europa.eu/lv/tools-and-publications/publications/l_en?text=Country%20report%20Worker%20participation%20in%20the%20management%20of%20occupational%20safety%20and%20health%20%E2%80%94%20qualitative%20evidence%20from%20ESENER-2&amp;sort_by=search_api_relevance&amp;direction=desc\">valsts ziņojumi</a>,&nbsp;<a href=\"https://osha.europa.eu/lv/tools-and-publications/publications/worker-participation-management-occupational-safety-and-health/view\">kopsavilkums</a>&nbsp;(2017.g.);</li><li><em>ESENER-2</em>&nbsp;kopīgā analīze,&nbsp;<em>LFS 2013</em>&nbsp;<em>ad hoc</em>&nbsp;modulis par negadījumiem darbavietā un citām ar darbu saistītajām veselības problēmām un sestais Eiropas darba apstākļu apsekojums —&nbsp;<a href=\"https://osha.europa.eu/lv/tools-and-publications/publications/health-and-safety-risks-workplace-joint-analysis-three-major/view\">pārskats</a>,&nbsp;<a href=\"https://osha.europa.eu/lv/tools-and-publications/publications/executive-summary-health-and-safety-risks-workplace-joint/view\">kopsavilkums</a>&nbsp;(2017.g.);</li><li>psihosociālo risku pārvaldība —&nbsp;<a href=\"https://osha.europa.eu/lv/tools-and-publications/publications/management-psychosocial-risks-european-workplaces-evidence/view\">pārskats</a>,&nbsp;<a href=\"https://osha.europa.eu/lv/tools-and-publications/publications/executive-summary-management-psychosocial-risks-european/view\">kopsavilkums&nbsp;</a>(2018.g.);</li><li>DDVA pārvaldība —&nbsp;<a href=\"https://osha.europa.eu/lv/tools-and-publications/publications/management-occupational-health-and-safety-european-workplaces/view\">pārskats</a>,&nbsp;<a href=\"https://osha.europa.eu/lv/tools-and-publications/public