INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LT", 0, "<ul type=\"disc\"><li>„Kokybinis ESENER apklausos (2014 m.) rezultatų po bandymo vertinimas“;&nbsp;<a href=\"https://osha.europa.eu/lt/tools-and-publications/publications/reports/qualitative-post-test-evaluation-of-esener-overview-report/view\">apžvalgos ataskaita</a>&nbsp;ir&nbsp;<a href=\"https://osha.europa.eu/lt/tools-and-publications/publications/reports/qualitative-post-test-evaluation-of-esener-substantive-findings/view\">pagrindinės išvados</a>&nbsp;<em>(anglų k.);</em></li><li>„Darbuotojų saugos ir sveikatos praktiką lemiančių veiksnių analizė pasirinktose ES valstybėse narėse (2013 m.)“;&nbsp;<a href=\"https://osha.europa.eu/lt/tools-and-publications/publications/reports/analysis-determinants-workplace-OSH-in-EU/view/\">apžvalgos ataskaita</a>&nbsp;<em>(anglų k.),&nbsp;</em><a href=\"https://osha.europa.eu/lt/tools-and-publications/publications/reports/summary-analysis-determinants-workplace-OSH-in-EU/view\">santrauka</a>&nbsp;<em>(8 kalbomis) ir&nbsp;</em><a href=\"https://osha.europa.eu/lt/tools-and-publications/publications/reports/annex-analysis-determinants-workplace-OSH-in-EU/view/\">priedas</a><em>&nbsp;(anglų k.);</em></li><li>keturių papildomų analizių ataskaitų santrauka: „ESENER įmonių apklausa (2012 m.). Saugos ir sveikatos darbe valdymo, psichosocialinės rizikos ir darbuotojų dalyvavimo reikšmės supratimas“;&nbsp;<a href=\"https://osha.europa.eu/lt/tools-and-publications/publications/reports/esener-summary/view\">santrauka</a>&nbsp;<em>(24 kalbomis);</em></li><li>„Atstovavimas darbuotojams ir jų konsultavimas saugos ir sveikatos klausimais. Europos įmonių apklausos apie naują ir kylančią riziką išvadų analizė (2012 m.)“;&nbsp;<a href=\"https://osha.europa.eu/lt/tools-and-publications/publications/reports/esener_workers-involvement/view\">apžvalgos ataskaita</a>&nbsp;<em>(anglų k.)&nbsp;</em>ir&nbsp;<a href=\"https://osha.europa.eu/lt/tools-and-public