INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "EN", 1, "<p>Access keys are a navigation device enabling you to get around this web site using your keyboard.</p><p>In-depth information about access keys can be found at&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines.</a></p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "BG", 0, "<p>Клавишите за достъп са средство за навигация, което дава възможност за ползване на уебсайта чрез клавиатурата.</p><p>За подробна информация относно клавишите за достъп на&nbsp;<span class=\"link-external\"><a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">Указания на W3C за достъп</a></span></p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "CS", 0, "<p>Přístupové klávesy jsou součástí navigace a umožňují vám pohybovat se na těchto internetových stránkách pomocí klávesových zkratek.</p><p>Podrobné informace o přístupových klávesách naleznete v části&nbsp;<span class=\"link-external\"><a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>&nbsp;</span>(Pokyny týkající se přístupu).</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "DA", 0, "<p>Genvejstaster gør det muligt at benytte tastaturet til at navigere på webstedet.</p><p>Der findes mere uddybende oplysninger om genvejstaster på&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "DE", 0, "<p>Zugriffsschlüssel („Access keys“) sind ein Navigationsmittel, mit dem Sie sich unter Verwendung Ihrer Tastatur auf dieser Website bewegen können.</p><p>Weitere Informationen über Zugriffsschlüssel finden Sie unter&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "ET", 0, "<p>Juurdepääsuklahvid on navigeerimisvahend, mis võimaldab veebisaidil klaviatuuri abil liikuda.</p><p>Üksikasjalikumat teavet juurdepääsuklahvide kohta leiate siit:&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C juurdepääsujuhised</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "EL", 0, "<p>Τα πλήκτρα πρόσβασης είναι ένα εργαλείο πλοήγησης που σας επιτρέπει να περιηγηθείτε σε αυτήν την ιστοθέση χρησιμοποιώντας το πληκτρολόγιό σας.</p><p>Περισσότερες πληροφορίες σχετικά με τα πλήκτρα πρόσβασης μπορείτε να βρείτε στις&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">Οδηγίες Προσβασιμότητας της Κοινοπραξίας του Παγκόσμιου Ιστού (W3C)</a></p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "ES", 0, "<p>Las teclas de navegación permiten a los usuarios navegar por este sitio web utilizando el teclado.</p><p>Si desea más información sobre las teclas de navegación, haga clic en&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "FR", 0, "<p>Les touches d'accès constituent un système de navigation qui vous permet de naviguer sur ce site web à l'aide de votre clavier.</p><p>Des informations détaillées sur les touches d'accès se trouvent dans les&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">Lignes directrices d'accessibilité de W3C</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "HR", 0, "<p>Pristupne tipke navigacijska su pomagala koja omogućuju kretanje po ovoj internetskoj stranici s pomoću tipkovnice.</p><p>Temeljitije informacije o pristupnim tipkama mogu se pronaći u&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">Smjernicama o pristupu sadržajima konzorcija W3C</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "IS", 0, "<p>Aðgangslyklar er vafrabúnaður til þess að gera þér kleift að fara um vefsíðuna með því að nota lyklaborðið þitt.</p><p>Ítarlegar upplýsingar um aðgangslykla má finna í&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">leiðbeiningum W3C um aðgengi</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "IT", 0, "<p>I tasti a scelta rapida sono uno strumento di navigazione per muoversi all’interno del sito web con l’ausilio della tastiera.</p><p>Per informazioni dettagliate sui tasti a scelta rapida, si rimanda alle&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LV", 0, "<p>Piekļuves taustiņi ir navigācijas ierīce, kas šajā tīmekļa vietnē ļauj pārvietoties ar klaviatūras palīdzību.</p><p>Sīkāka informācija par piekļuves taustiņiem ir atrodama tīmekļa vietnē&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\"><em>W3C Accessibility Guidelines</em></a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "LT", 0, "<p>Prieigos klavišai yra susieti su naršymo funkcijomis, kurios leidžia jums naudojantis klaviatūra ieškoti informacijos šioje svetainėje.</p><p>Išsamią informaciją apie prieigos klavišus galima rasti&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C prieinamumo gairėse</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "HU", 0, "<p>Az alábbi billentyűk és billentyűkombinációk a honlapon való navigálás megkönnyítésére szolgálnak.</p><p>Részletes információ a billentyűkombinációkról a&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>&nbsp;címen található.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "MT", 0, "<p>Il-kodiċi ta' aċċess huma strument ta' navigazzjoni li jippermettulek sabiex tuża u tesplora dan il-websajt billi tuża t-tastiera tiegħek.</p><p>Għal iktar dettalji dwar dawn il-kodiċi ta' aċċess tista' tmur fuq&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">Linji gwida ta' Aċċessibbiltà W3C</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "NL", 0, "<p>Toegangstoetsen zijn een navigatiehulpmiddel waarmee het mogelijk is op deze website te surfen met gebruikmaking van uw toetsenbord.</p><p>Nadere informatie over toegangstoetsen kunt u vinden op&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "NO", 0, "<p>Tilgjengelighetstaster er en funksjon som lar deg navigere på nettstedet ved bruk av tastaturet.</p><p>For nærmere informasjon om tilgjengelighetstaster, se&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3Cs retningslinjer for tilgjengelighet</a>&nbsp;(på engelsk).</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "PL", 0, "<p>Klucze dostępu są urządzeniem nawigacyjnym umożliwiającym poruszanie się po tej witrynie internetowej za pomocą klawiatury.</p><p>Obszerne informacje na temat kluczy dostępu znajdują się w&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "PT", 0, "<p>As teclas de acesso direto são um dispositivo de navegação que permite navegar neste sítio Web utilizando o teclado.</p><p>Podem ser consultadas todas as informações sobre teclas de acesso direto em&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">Diretrizes sobre a acessibilidade W3C</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "RO", 0, "<p>Tastele de acces reprezintă un instrument care vă permite să navigați prin acest site cu ajutorul tastaturii.</p><p>Puteți găsi informații detaliate despre tastele de acces la adresa&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C – Orientări privind accesibilitatea</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SK", 0, "<p>Prístupové klávesy sú navigačným nástrojom, ktorý vám pomôže pohybovať sa po tejto webovej stránke pomocou klávesnice.</p><p>Podrobné informácie o prístupových klávesách sa nachádzajú v dokumente&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Pokyny pre dostupnosť</a></p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SL", 0, "<p>Tipke za dostop so navigacijsko orodje, ki omogoča pomikanje po tej spletni strani z uporabo tipkovnice.</p><p>Podrobne informacije o tipkah za dostop lahko najdete v&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">Smernicah za dostop W3C</a>.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "FI", 0, "<p>Valintanäppäinten avulla voit selata verkkosivustoa näppäimistösi kautta.</p><p>Tarkempia tietoja valintanäppäimistä on saatavilla&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>&nbsp;-ohjeissa.</p>");
INSERT INTO translation (literal_id, language, is_default, text) VALUES (@literalId, "SV", 0, "<p>Snabbtangenter är ett sätt att navigera på webbplatsen genom att använda tangentbordet.</p><p>Mer information om snabbtangenter (access keys) hittar du i&nbsp;<a href=\"http://www.w3.org/TR/WCAG10-HTML-TECHS/#link-accesskey\" target=\"_blank\">W3C Accessibility Guidelines</a>.</p>");
